package nested_loop;

public class pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=7;                                  //left decrement triangle//
		 for(int i=0; i<n; i++)
		 {
		  for(int j=0; j<n-i; j++)	 
		  {
		 System.out.print("*");
		 
	     }
         System.out.println();
}
	}	
}